    @NonNull
    private List<WeightedLatLng> generateHeatMapData() {
        List<WeightedLatLng> data = new ArrayList<>();
        int squareWidth=displayWidth/32;//小矩形的宽度
        int squareHeight=displayHeight/32;//小矩形的高度
        int iv=squareWidth/2;

        Log.e("HHHH","generateData.....");
        Log.e("HHHH","generateData.....");
        for (int i = 0; i < test_data.length; i++) {
            int xIndex = i % 32;
            int yIndex = (i / 32);
            int x=xIndex* squareWidth+iv;
            int y=yIndex* squareHeight+iv;
            float value=test_data[i];
            data.add(new WeightedLatLng(x,
                    y,
                    value ));
        }
        Log.e("HHHH",data.toString());
        return data;
    }
